        [Fact]
        public void ToUnit()
        {
            var weber = MagneticFlux.FromWebers(1);

            var weberQuantity = weber.ToUnit(MagneticFluxUnit.Weber);
            AssertEx.EqualTolerance(WebersInOneWeber, (double)weberQuantity.Value, WebersTolerance);
